mapper.select:
SELECT a.tid AS 'site.tid',a.tname AS 'site.tname',a.tintro AS 'site.tintro', b.`uid` AS 'user.uid',b.`uname` AS 'user.uname',b.`email` AS 'user.email' FROM (SELECT s.`tid`,s.`tname`,s.`tintro`,t.`uid` FROM t_site s LEFT JOIN t_site_user t ON s.`tid`=t.`tid`) a LEFT JOIN t_user b ON a.uid=b.`uid`
a.tname LIKE '%${name}%' OR b.`uname` LIKE '%${name}%'
LIMIT #{index},#{pageSize}
